import React from 'react';
import logo from '../images/logo.jpeg';
import '../css/header.css';
import { useNavigate } from 'react-router-dom';

function Header() {
  const navigate = useNavigate();

  const handleHeaderButton = ({ target: { value } }) => {
    if (value !== "Loja") {
      return navigate("/maintenence");
    }

    return navigate("/");
  };

  const handleSearchs = (name) => {
    console.log(name);
  };

  return(
    <header className="header-container">
      <div>
        <img
          src={ logo }
          alt="Logo Wine - O maior clube de vinhos do mundo"
          width="100px"
        />
      </div>
      <nav>
        <button
          type="button"
          id="header-buttons"
          value="Clube"
          onClick={ (e) => handleHeaderButton(e) }
        >
          Clube
        </button>
        <button
          type="button"
          id="header-buttons"
          value="Loja"
          onClick={ (e) => handleHeaderButton(e) }
        >
          Loja
        </button>
        <button
          type="button"
          id="header-buttons"
          value="Produtores"
          onClick={ (e) => handleHeaderButton(e) }
        >
          Produtores
        </button>
        <button
          type="button"
          id="header-buttons"
          value="Ofertas"
          onClick={ (e) => handleHeaderButton(e) }
        >
          Ofertas
        </button>
        <button
          type="button"
          id="header-buttons"
          value="Eventos"
          onClick={ handleHeaderButton }
        >
          Eventos
        </button>
      </nav>
      <nav>
        <button
          id="icons-buttons"
          name="search"
          onClick={ (e) => handleSearchs(e.target.name) }
        >
          <i className="fas fa-search" />
        </button>
        <button
          id="icons-buttons"
          name="user"
          onClick={ (e) => handleSearchs(e.name) }
        >
          <i className="far fa-user" />
        </button>
        <button
          id="icons-buttons"
          name="cart"
          onClick={ (e) => handleSearchs(e.name) }
        >
        <i className="fa fa-shopping-cart" />
        </button>
      </nav>
    </header>

  );
}

export default Header;
