import React, { useEffect, useState } from 'react';
import { getItemsSold, setItemsSold } from '../utils/localStorage';
import { Table } from 'react-bootstrap';

function Cart() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    const soldItems = getItemsSold();
    setItems(soldItems);
  }, [])

  const handleDelete = (productId) => {
    const deleteItem = items.filter((item) => item.id !== productId);
    setItems(deleteItem);
    return setItemsSold(deleteItem);
  };

  const totalValue = () => {
    const total = items
      .reduce((prev, { price, quantity}) => prev + (price * quantity), 0)
      .toFixed(2);

      return total;
  }

  return (
    <main>
      <h3>Finalizar Pedido</h3>
      <Table striped bordered hover>
        <thead>
            <tr>
              <th>Item</th>
              <th>Nome</th>
              <th>Quantidade</th>
              <th>Valor Unit√°rio</th>
              <th>Sub-total</th>
              <th>Remover Item</th>
            </tr>
          </thead>
          <tbody>
            {items.map((item, idx) => (
              <tr key={ idx }>
                <td>{ idx + 1 }</td>
                <td>{ item.name }</td>
                <td>{ item.quantity }</td>
                <td>{ (item.price).toFixed(2) }</td>
                <td>{ (item.price * item.quantity).toFixed(2) }</td>
                <td>
                  <button
                    type="button"
                    onClick={ () => handleDelete(item.id) }
                  >
                    Remover
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
      </Table>
    </main>
  );
}

export default Cart;
