import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import myContext from '../context/myContext';
import StarCreator from '../utils/StarCreator';
import star from '../images/star.png';

function Details() {
  const { product } = useContext(myContext);
  const [quantity, setQuantity] = useState(1);

  const handleLess = () => {
    if (quantity === 0) return null;
    setQuantity(quantity - 1);
  };

  const handleMore = () => {
    setQuantity(quantity + 1);
  };

  return (
    <main>
      <nav>
        <Link to="/">
          { `<< Voltar` }
        </Link>
      </nav>
      <section>
        <img src={ product.image } alt={ product.name } />
        <aside>
          <section>
            <p>{ `Vinhos > ${product.country} >` }</p>
            <span>{ product.region }</span>
          </section>
          <h2>{ product.name }</h2>
          <section>
            <img src={ product.flag } alt={ `Bandeira ${product.country}` } width="15px" />
            <span>{ `${product.country} ${product.type} ${product.classification} ${product.size}` }</span>
            { StarCreator(product.rating).map((item) => {
              if (!item) return null;
              <img key={ item } src={ star } alt="estrela dourada" width="20px" />
            }) }
          </section>
          <h1>{`R$ ${(product.priceMember).toFixed(2)}` }</h1>
          <span>{ `NÃO SÓCIO R$ ${(product.priceNonMember).toFixed(2)}/UN.` }</span>
          <h4>Comentário do Sommelier</h4>
          <p>{ product.sommelierComment }</p>
          <button type="button" onClick={ handleLess }>-</button>
          <input type="number" value={ quantity } />
          <button type="button" onClick={ handleMore }>+</button>
        </aside>
      </section>
    </main>
  );
}

export default Details;
