import React, { useContext } from 'react';
import logo from '../images/logo.jpeg';
import { useNavigate } from 'react-router-dom';
import { SearchBar } from './sub-components';
import myContext from '../context/myContext';
import '../css/header.css';
import { Container, Nav, Navbar } from 'react-bootstrap';

function Header() {
  const { show, setShow } = useContext(myContext);
  const navigate = useNavigate();

  const handleHeaderButton = async ({ target: { value } }) => {
    if (value !== "Loja") {
      return navigate("/maintenence");
    }

    // pesquisa sobre reload de pag
    // https://pt.stackoverflow.com/questions/251826/reload-de-p%C3%A1gina-web
    window.location.reload();
  };

  const handleSearch = () => {
    setShow(!show);
  };

  const handleProfile = () => {
    navigate("/maintenence");
  };

  const handleCart = () => {
    navigate("/cart");
  };

  return(
    <Navbar bg="light" expand="lg">
      <Container fluid>
        <Navbar.Brand href="#">
          <img
            src={ logo }
            alt="Logo Wine - O maior clube de vinhos do mundo"
            width="100px"
          />          
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
        <Nav
          className="me-auto my-2 my-lg-0"
          style={{ maxHeight: '100px' }}
          navbarScroll
        >
          <Nav.Link href="#action1">
            <button
              type="button"
              id="header-buttons"
              value="Clube"
              onClick={ (e) => handleHeaderButton(e) }
            >
              Clube
            </button>
          </Nav.Link>
          <Nav.Link href="#action2">
            <button
              type="button"
              id="header-buttons"
              value="Loja"
              onClick={ (e) => handleHeaderButton(e) }
            >
              Loja
            </button>            
          </Nav.Link>
          <Nav.Link href="#action2">
            <button
              type="button"
              id="header-buttons"
              value="Produtores"
              onClick={ (e) => handleHeaderButton(e) }
            >
              Produtores
            </button>
            <Nav.Link href="#action2">
              <button
                type="button"
                id="header-buttons"
                value="Ofertas"
                onClick={ (e) => handleHeaderButton(e) }
              >
                Ofertas
              </button>
              <Nav.Link href="#action2">
                <button
                  type="button"
                  id="header-buttons"
                  value="Eventos"
                  onClick={ handleHeaderButton }
                >
                  Eventos
                </button>
              </Nav.Link>            
            </Nav.Link>            
          </Nav.Link>
        </Nav>
        <Nav
          className="me-auto my-2 my-lg-0"
          style={{ maxHeight: '100px' }}
          navbarScroll
        >
          <Nav.Link href="#action1">
            <button
              id="icons-buttons"
              onClick={ handleSearch }
            >
              <i className="fas fa-search" />
            </button>
          </Nav.Link>
          <Nav.Link href="#action1">
            <button
              id="icons-buttons"
              onClick={ handleProfile }
            >
              <i className="far fa-user" />
            </button>
          </Nav.Link>
          <Nav.Link href="#action1">
            <button
              id="icons-buttons"
              name="cart"
              onClick={ handleCart }
            >
            <i className="fa fa-shopping-cart" />
            </button>
          </Nav.Link>
        </Nav>
        </Navbar.Collapse>
      </Container>
      { show && <SearchBar /> }
    </Navbar>

  );
}

export default Header;
