import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import myContext from '../context/myContext';
import StarCreator from '../utils/StarCreator';
import star from '../images/star.png';

function Details() {
  const { product } = useContext(myContext);
  console.log(product);

  return (
    <main>
      <nav>
        <Link to="/">
          { `<< Voltar` }
        </Link>
      </nav>
      <section>
        <img src={ product.image } alt={ product.name } />
        <aside>
          <section>
            <p>{ `Vinhos > ${product.country} >` }</p>
            <span>{ product.region }</span>
          </section>
          <h2>{ product.name }</h2>
          <section>
            <img src={ product.flag } alt={ `Bandeira ${product.country}` } width="15px" />
            <span>{ `${product.country} ${product.type} ${product.classification} ${product.size}` }</span>
            { StarCreator(product.rating).map((star) => (
              <img key="rating" src={ star } alt="" />
            )) }
          </section>
        </aside>
      </section>
    </main>
  );
}

export default Details;
